import {createStore, applyMiddleware} from 'redux'
import thunk from 'redux-thunk'
import  rootReducer from './reducer/rootReducer'

export default function Store(initialState = {}){
    return createStore(
        rootReducer,
        initialState,
        applyMiddleware(thunk)
    )
}
